const burgerBtn=document.querySelector(".menu-button"),burgerDisplay=document.querySelector(".nav-burger"),crossIconBurger=document.querySelector(".cross-icon-burger");burgerBtn.addEventListener("click",(function(e){burgerDisplay.classList.add("active"),document.body.style.overflow="hidden"})),crossIconBurger.addEventListener("click",(function(e){burgerDisplay.classList.remove("active"),document.body.style.overflow="auto"}));