const playbtn=document.querySelector("#home-play-button"),modalVideo=document.querySelector("#modal-video-bondi"),videoElement=modalVideo.querySelector("video");playbtn.addEventListener("click",function(e){modalVideo.style.display="block",videoElement.play()}),document.addEventListener("keydown",function(e){"Escape"===e.key&&(modalVideo.style.display="none",videoElement.pause())});